mapper.insert:
INSERT INTO products
(product_id, c_id, user_id, p_name, p_info, p_created_at, p_sale_type, p_account, p_address, p_bank, p_address_sub, p_addr_postcode)
VALUES (
(SELECT COALESCE(MAX(product_id), 0) + 1 FROM products),
#{cId}, #{userId}, #{pName}, #{pInfo}, sysdate, #{pSaleType}, #{pAccount}, #{pAddress}, #{pBank}, #{pAddressSub}, #{pAddrPostcode}
);
