mapper.select:
select a.current_amount,a.user_bids_id, u.u_nickname u_bids_nickname
from (select amount current_amount, user_id user_bids_id
    from bids
    where product_id = #{param1}
    and amount = (
    select max(amount)
    from bids
    where product_id = #{param1}
)) a
join users u on u.user_id = a.user_bids_id
